import React from 'react';
import './RecipePage.css';

class RecipePage extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            props: this.props
        }
    }

    render() {
        return (
            <div className="tile">
                <img className="image" src={"https://spoonacular.com/recipeImages/" + this.state.props.recipeID + "-480x360" + "." +this.state.props.format} />
                <button type="button" className="title" onClick={this.displayRecipePage} >  {this.state.props.recipeName} </button>
            </div>
        )
    }
}

export default Recipe;
