import React from 'react';
import './Recipe.css';

class Recipe extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            props: this.props
        }
    }
    displayRecipePage = () => {
        this.setState(state => ({ recipePageBool: !state.isShow }));
      };
    render() {
        return (
            <div className="tile">
                <img className="image" src={"https://spoonacular.com/recipeImages/" + this.state.props.recipeID + "-480x360" + "." + this.state.props.format} />
                <p className="title" onClick={this.displayRecipePage} >  {this.state.props.recipeName} </p>
            </div>
        )
    }
}

export default Recipe;
