import React, { Component } from "react";
import './App.css';
import SearchBar from '../components/SearchBar/SearchBar'; 
import SubmitButton from '../components/SubmitButton/SubmitButton'; 
import Navbar from "../components/Navbar/Navbar";
import api from '../apis/api';
import Recipe from '../components/Recipe/Recipe'; 
import Select from 'react-select'; 
import RecipePage from '../components/RecipePage/RecipePage';

class App extends Component {
  state = {
    recipes: [],
    selectedVideo: null,
    recipesBool: false,
    recipePageBool: true
}
handleSubmit = async (termFromSearchBar) => {
  const response = await api.get('/search', {
      params: {
          query: termFromSearchBar
      }
  })
  if(response.data.results.length > 0) {
    this.setState({
      recipes : response.data.results,
      recipesBool: true 
    }) 
    }
    // else {
    //    tell user stop doing stupid stuff 
    //    create card which will show user blah blah.. 
    // }
};


  render() {
    let displayedRecipes = null;
    let { recipes } = this.state; 
    let displaySelectedRecipe = null; 

    console.log(recipes)

    //Testing new component: 

    const options = [
      { value: 'chocolate', label: 'Chocolate' },
      { value: 'strawberry', label: 'Strawberry' },
      { value: 'vanilla', label: 'Vanilla' }
    ]

    const MyComponent = () => (
      <div className="select"> 
      <Select options={options} />
      </div> 
    )


    if(this.state.recipesBool) {
      displayedRecipes = (
        <div className="tileHolder">
          {
            recipes.map( recipe => 
              <Recipe key={recipe.title} recipeName={recipe.title} recipeID={recipe.id} format={recipe.image.split(".")[1]}> </Recipe>
            )
          }
        </div>
      )
    }
    if(this.state.recipePageBool) {
      displaySelectedRecipe = (
          <div> 
              <RecipePage> </RecipePage>
          </div>
      )
      this.setState(
        {
          answerBool: false,
          questionBool: false,
          wrongAnswerBool: true,
          returnToPreviousBool: true,
          nextBool: false
        })
    }
    console.log(this.state.recipes)


    return (
      <div className="App">
        <Navbar> </Navbar> 
        <SearchBar handleFormSubmit={this.handleSubmit}></SearchBar>
        {/* <SubmitButton  > </SubmitButton> */}
        {displayedRecipes}
        {MyComponent}
        {displaySelectedRecipe}
      </div>
    );
  }
}

export default App;
